const reportData = $input.first().json;

// Classe pour la g√©n√©ration de donn√©es dashboard
class DashboardGenerator {
  constructor(data) {
    this.data = data;
    this.dashboard = {};
  }

  // G√©n√©ration des m√©triques pour dashboard
  generateDashboardMetrics() {
    this.dashboard = {
      overview: {
        title: 'n8n Ecosystem Overview',
        timestamp: this.data.analysis_metadata.timestamp,
        kpis: [
          {
            label: 'GitHub Stars',
            value: this.data.github_ecosystem.n8n_core.stars,
            change: `+${this.data.github_ecosystem.trends.star_growth_rate}%`,
            trend: 'up'
          },
          {
            label: 'Community Nodes',
            value: this.data.github_ecosystem.community_nodes.total_packages,
            change: `+${this.data.github_ecosystem.community_nodes.new_this_month}`,
            trend: 'up'
          },
          {
            label: 'Market Size',
            value: `$${this.data.market_analysis.automation_market.size_billion}B`,
            change: `+${this.data.market_analysis.automation_market.growth_rate}%`,
            trend: 'up'
          },
          {
            label: 'Community Score',
            value: Math.round(this.data.github_ecosystem.trends.community_engagement),
            change: '+5%',
            trend: 'up'
          }
        ]
      },

      github_metrics: {
        title: 'GitHub Ecosystem',
        charts: [
          {
            type: 'line',
            title: 'Stars Growth',
            data: this.generateStarsGrowthData()
          },
          {
            type: 'bar',
            title: 'Contributors by Month',
            data: this.generateContributorsData()
          },
          {
            type: 'doughnut',
            title: 'Issue Status',
            data: this.generateIssueStatusData()
          }
        ]
      },

      market_analysis: {
        title: 'Market Analysis',
        charts: [
          {
            type: 'pie',
            title: 'Market Share by Competitor',
            data: this.generateMarketShareData()
          },
          {
            type: 'bar',
            title: 'Technology Trends Adoption',
            data: this.generateTechTrendsData()
          },
          {
            type: 'radar',
            title: 'SWOT Analysis',
            data: this.generateSWOTData()
          }
        ]
      },

      community_metrics: {
        title: 'Community Engagement',
        charts: [
          {
            type: 'area',
            title: 'Discord Members Growth',
            data: this.generateDiscordGrowthData()
          },
          {
            type: 'bar',
            title: 'Platform Engagement',
            data: this.generatePlatformEngagementData()
          }
        ]
      },

      predictions: {
        title: 'Predictions & Forecasts',
        charts: [
          {
            type: 'line',
            title: 'Market Growth Prediction',
            data: this.generateMarketGrowthPrediction()
          },
          {
            type: 'gauge',
            title: 'Confidence Levels',
            data: this.generateConfidenceData()
          }
        ]
      }
    };

    return this;
  }

  // G√©n√©ration des donn√©es de croissance stars
  generateStarsGrowthData() {
    const baseStars = this.data.github_ecosystem.n8n_core.stars;
    const growthRate = this.data.github_ecosystem.trends.star_growth_rate;

    return {
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets: [{
        label: 'Stars',
        data: [
          Math.round(baseStars * 0.85),
          Math.round(baseStars * 0.90),
          Math.round(baseStars * 0.94),
          Math.round(baseStars * 0.97),
          Math.round(baseStars * 0.99),
          baseStars
        ],
        borderColor: '#4285f4',
        backgroundColor: 'rgba(66, 133, 244, 0.1)'
      }]
    };
  }

  // G√©n√©ration des donn√©es contributeurs
  generateContributorsData() {
    return {
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets: [{
        label: 'Active Contributors',
        data: [45, 52, 48, 61, 67, 58],
        backgroundColor: '#34a853'
      }]
    };
  }

  // G√©n√©ration des donn√©es statut issues
  generateIssueStatusData() {
    const openIssues = this.data.github_ecosystem.n8n_core.open_issues;
    const closedIssues = this.data.github_ecosystem.n8n_core.closed_issues;

    return {
      labels: ['Open', 'Closed'],
      datasets: [{
        data: [openIssues, closedIssues],
        backgroundColor: ['#ea4335', '#34a853']
      }]
    };
  }

  // G√©n√©ration des donn√©es part de march√©
  generateMarketShareData() {
    const competitors = this.data.market_analysis.competitors;

    return {
      labels: Object.keys(competitors),
      datasets: [{
        data: Object.values(competitors).map(comp => comp.market_share),
        backgroundColor: ['#4285f4', '#ea4335', '#fbbc04', '#34a853']
      }]
    };
  }

  // G√©n√©ration des donn√©es tendances tech
  generateTechTrendsData() {
    const trends = this.data.technology_trends.emerging_technologies;

    return {
      labels: trends.map(trend => trend.name),
      datasets: [{
        label: 'Adoption Rate (%)',
        data: trends.map(trend => trend.adoption_rate),
        backgroundColor: '#4285f4'
      }]
    };
  }

  // G√©n√©ration des donn√©es SWOT
  generateSWOTData() {
    return {
      labels: ['Strengths', 'Weaknesses', 'Opportunities', 'Threats'],
      datasets: [{
        label: 'SWOT Analysis',
        data: [85, 45, 78, 35], // Scores simul√©s
        backgroundColor: 'rgba(66, 133, 244, 0.2)',
        borderColor: '#4285f4'
      }]
    };
  }

  // G√©n√©ration des donn√©es croissance Discord
  generateDiscordGrowthData() {
    const currentMembers = this.data.community_metrics.discord.members;

    return {
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets: [{
        label: 'Discord Members',
        data: [
          Math.round(currentMembers * 0.75),
          Math.round(currentMembers * 0.82),
          Math.round(currentMembers * 0.88),
          Math.round(currentMembers * 0.93),
          Math.round(currentMembers * 0.97),
          currentMembers
        ],
        borderColor: '#7289da',
        backgroundColor: 'rgba(114, 137, 218, 0.1)'
      }]
    };
  }

  // G√©n√©ration des donn√©es engagement plateforme
  generatePlatformEngagementData() {
    const community = this.data.community_metrics;

    return {
      labels: ['Discord', 'Forum', 'Reddit', 'YouTube'],
      datasets: [{
        label: 'Engagement Score',
        data: [85, 72, 68, 78], // Scores calcul√©s
        backgroundColor: ['#7289da', '#4285f4', '#ff4500', '#ff0000']
      }]
    };
  }

  // G√©n√©ration des pr√©dictions croissance march√©
  generateMarketGrowthPrediction() {
    const currentSize = this.data.market_analysis.automation_market.size_billion;
    const expectedSize = this.data.market_analysis.automation_market.expected_size_2027;

    return {
      labels: ['2023', '2024', '2025', '2026', '2027'],
      datasets: [{
        label: 'Market Size ($B)',
        data: [
          currentSize * 0.8,
          currentSize,
          currentSize * 1.25,
          currentSize * 1.55,
          expectedSize
        ],
        borderColor: '#34a853',
        backgroundColor: 'rgba(52, 168, 83, 0.1)'
      }]
    };
  }

  // G√©n√©ration des donn√©es confiance
  generateConfidenceData() {
    const predictions = this.data.predictions;

    return {
      labels: ['Short Term', 'Medium Term', 'Long Term'],
      datasets: [{
        data: [
          predictions.short_term.confidence,
          predictions.medium_term.confidence,
          predictions.long_term.confidence
        ],
        backgroundColor: ['#34a853', '#fbbc04', '#ea4335']
      }]
    };
  }

  // G√©n√©ration du rapport dashboard
  generateDashboardReport() {
    return {
      dashboard_config: this.dashboard,
      export_formats: {
        json: true,
        pdf: true,
        png: true,
        csv: true
      },
      sharing_options: {
        public_link: false,
        team_access: true,
        embeddable: true
      },
      update_frequency: 'every_6_hours',
      generated_at: new Date().toISOString()
    };
  }
}

