const recordId = $json.id;  // Only if you have this field (otherwise remove this line)
const scenes = $json.output;  // No nested .output.output

return scenes.map((scene, index) => {
  return {
    json: {
      scene_number: index + 1,
      image_prompt: scene.image_prompt,
      movement_description: scene.movement_description,
      voiceover_script: scene.voiceover_script,
      originalRecordId: recordId // Optional: if you have it
    }
  };
});

const duration = 5000; // milliseconds for each video, adjust as needed
let timestamp = 0;

const keyframes = items.map((item, i) => {
  const url = item.json.video.url;
  const keyframe = {
    url: url,
    timestamp: timestamp,
    duration: duration
  };
  timestamp += duration;
  return keyframe;
});

return [{
  json: {
    tracks: [
      {
        id: "",
        type: "video",
        keyframes: keyframes
      }
    ]
  }
}];

