// Créer un fichier .eml pour le brouillon
const emailData = $node["Lookup for Send"].json;
const draft = $json.brouillon;

const emlContent = `From: greg@agsteeltrading.com
To: ${emailData.Nom}
Subject: Re: ${emailData["Email Reçu"].split('\n')[0]}
Date: ${new Date().toUTCString()}
Message-ID: <${Date.now()}@agsteeltrading.com>
MIME-Version: 1.0
Content-Type: text/plain; charset=UTF-8
Content-Transfer-Encoding: quoted-printable
X-Unsent: 1

${draft}`;

return [{
  json: {
    emlContent,
    filename: `brouillon-${$json.mailId}.eml`,
    mailId: $json.mailId,
    chatId: $json.chatId
  }
}];
